/*
 * Minimal JavaScript for DOE application navigation.
 *
 * This script provides a basic implementation of the navbar toggler
 * behaviour found in Bootstrap. It toggles the display of the
 * collapsible navigation menu when the user clicks the hamburger
 * button on small screens. Because this application uses only a
 * handful of Bootstrap features, there is no need to include the
 * full Bootstrap JavaScript bundle.  Additional functionality can
 * be added here if more interactive components are required.
 */

document.addEventListener('DOMContentLoaded', function () {
  var toggler = document.querySelector('.navbar-toggler');
  var nav = document.getElementById('navbarNav');
  if (toggler && nav) {
    toggler.addEventListener('click', function () {
      if (nav.classList.contains('show')) {
        nav.classList.remove('show');
      } else {
        nav.classList.add('show');
      }
    });
  }
});